# main function to differentiate between languages with the same scripts
def branch(sentence, root):
    word_tokens = sentence.split(" ")
    if root == "latin":
        french_word_list = FRENCH_WORD_STRING.split(" ")
        english_word_list = ENGLISH_WORD_STRING.split(" ")
        spanish_word_list = SPANISH_WORD_STRING.split(" ")
